using System.ComponentModel.DataAnnotations;

namespace OmegaTerminal.Core.Models;

public record Scene
{
    [Required] public string Id { get; init; }
    [Required] public string Title { get; init; }
    [Required] public UiStyle UiStyle { get; init; }

    public List<DialogueLine> Dialogue { get; init; } = [];
    public bool Interactive { get; init; } = false;
    public List<string>? Options { get; init; }

    // Arbitrary metadata (e.g., hp_change, enemy_name)
    public Dictionary<string, object> Metadata { get; init; } = [];

    [Required] public string NextSceneId { get; init; }
}
```

