## âš™ï¸ Engine (`OmegaTerminal.Core/Engine/OmegaEngine.cs`)

```csharp
namespace OmegaTerminal.Core.Engine;

public class OmegaEngine
{
    private readonly SceneLoader _loader;
    private readonly Dictionary<UiStyle, IUiRenderer> _renderers;
    private readonly Dictionary<string, object> _state = new()
    {
        ["hp"] = 100
    };

    public OmegaEngine(SceneLoader loader, IEnumerable<IUiRenderer> renderers)
    {
        _loader = loader;
        _renderers = renderers.ToDictionary(r => r switch
        {
            CrtRenderer => UiStyle.Crt,
            GlitchRenderer => UiStyle.Glitch,
            _ => throw new ArgumentException("Unknown renderer")
        });
    }

    public async Task RunAsync(string startSceneId = "scene_01")
    {
        var currentSceneId = startSceneId;
        while (!string.IsNullOrEmpty(currentSceneId))
        {
            var scene = _loader.Load(currentSceneId);
            var renderer = _renderers[scene.UiStyle];

            renderer.Render(scene);
            await renderer.WaitForInputAsync(scene);

            // Apply side effects
            if (scene.Metadata.TryGetValue("hp_loss", out var hpLossObj) && 
                hpLossObj is int hpLoss)
            {
                _state["hp"] = (int)_state["hp"] - hpLoss;
            }

            currentSceneId = scene.NextSceneId;
        }
    }
}
```

