[Fact]
public void Loading_Scene05_IncludesHpLossMetadata()
{
    var loader = new SceneLoader("scenes");
    var scene = loader.Load("scene_05");

    Assert.Equal("scene_06", scene.NextSceneId);
    Assert.True(scene.Metadata.TryGetValue("hp_loss", out var hpLoss));
    Assert.Equal(30, hpLoss);
}

